'use strict';

var gutil = require('gulp-util');
var through = require('through2');

function removeCssComments( str, opts ) {
  return str.replace(/\/\*[\s\S]*?\*\/\n|([^:]|^)\/\/.*\n$/g, '').replace(/\\n/, '');
}

module.exports = function (opts) {
  return through.obj(function (file, enc, cb) {
    if (file.isNull()) {
      cb(null, file);
    }

    if (file.isStream()) {
      cb(new gutil.PluginError('gulp-remove-css-comments', 'Streaming not supported'));
      return;
    }

    try {
      file.contents = new Buffer(removeCssComments(file.contents.toString()));
      this.push(file);
    } catch (err) {
      this.emit('error', new gutil.PluginError('gulp-remove-css-comments', err, {fileName: file.path}));
    }

    cb();
  });
};